import React, { useEffect, useState } from "react";
import "./pages.css";
import { TabList, Tab, Widget, Tag, Table, Form } from "web3uikit";
import { useNativeBalance, useMoralis } from "react-moralis";


const Home = () => {
  const [latestBalance, setLatestBalance] = useState();
  const { getBalances, data: balance, nativeToken, error, isLoading } = useNativeBalance({ chain: "mumbai" });
  useEffect(() => {

    {
      if (balance.balance)
        setLatestBalance(Moralis.Units.FromWei(balance.balance))
    }
  }, [])
  const { Moralis } = useMoralis();
  return (
    <>
      <div className="content">
        {console.log("Test", latestBalance)}
        <TabList defaultActiveKey={1} tabStyle="bulbUnion">
          <Tab tabKey={1} tabName="DAO"></Tab>
          <Tab tabKey={2} tabName="FORUM"></Tab>
          <Tab tabKey={3} tabName="DOCS"></Tab>
        </TabList>
      </div>
    </>
  );
};

export default Home;
